#[test]
#[cfg(all(any(target_arch = "x86", target_arch = "x86_64"), target_feature = "sse2"))]
fn _mm_slli_epi8_test() {
    let arr: int8x16 = int8x16 {
        0: [
            0x00, 0x01, 0x02, 0x03, 0x04, 0x05, 0x06, 0x07, 0x08, 0x09, 0x0A, 0x0B, 0x0C, 0x0D, 0x0E, 0x0F
        ]
    };

    let v: __m128i = unsafe { _mm_load_si128(arr.0.as_ptr().cast::<__m128i>()) };

    assert_eq!(unsafe { transmute::<__m128i, [u8; 16_usize]>(_mm_slli_epi8(v, 1)) }, [0x00, 0x02, 0x04, 0x06, 0x08, 0x0a, 0x0c, 0x0e, 0x10, 0x12, 0x14, 0x16, 0x18, 0x1a, 0x1c, 0x1e]);
    assert_eq!(unsafe { transmute::<__m128i, [u8; 16_usize]>(_mm_slli_epi8(v, 2)) }, [0x00, 0x04, 0x08, 0x0c, 0x10, 0x14, 0x18, 0x1c, 0x20, 0x24, 0x28, 0x2c, 0x30, 0x34, 0x38, 0x3c]);
    assert_eq!(unsafe { transmute::<__m128i, [u8; 16_usize]>(_mm_slli_epi8(v, 3)) }, [0x00, 0x08, 0x10, 0x18, 0x20, 0x28, 0x30, 0x38, 0x40, 0x48, 0x50, 0x58, 0x60, 0x68, 0x70, 0x78]);
    assert_eq!(unsafe { transmute::<__m128i, [u8; 16_usize]>(_mm_slli_epi8(v, 4)) }, [0x00, 0x10, 0x20, 0x30, 0x40, 0x50, 0x60, 0x70, 0x80, 0x90, 0xa0, 0xb0, 0xc0, 0xd0, 0xe0, 0xf0]);
    assert_eq!(unsafe { transmute::<__m128i, [u8; 16_usize]>(_mm_slli_epi8(v, 5)) }, [0x00, 0x20, 0x40, 0x60, 0x80, 0xa0, 0xc0, 0xe0, 0x00, 0x20, 0x40, 0x60, 0x80, 0xa0, 0xc0, 0xe0]);
    assert_eq!(unsafe { transmute::<__m128i, [u8; 16_usize]>(_mm_slli_epi8(v, 6)) }, [0x00, 0x40, 0x80, 0xc0, 0x00, 0x40, 0x80, 0xc0, 0x00, 0x40, 0x80, 0xc0, 0x00, 0x40, 0x80, 0xc0]);
    assert_eq!(unsafe { transmute::<__m128i, [u8; 16_usize]>(_mm_slli_epi8(v, 7)) }, [0x00, 0x80, 0x00, 0x80, 0x00, 0x80, 0x00, 0x80, 0x00, 0x80, 0x00, 0x80, 0x00, 0x80, 0x00, 0x80]);
}

